'use strict';

var assert = require('assert');
var { getTimestamp, createObjectId } = require('./');

assert.equal(getTimestamp('563229dd1ee6030100644cbe'), 1446128093391);
assert.equal(getTimestamp('563228c91ee6030100644cbc'), 1446127817391);
assert.equal(getTimestamp('55d700442a333f2f0a5b8e74'), 1440153668357);

try {
	getTimestamp('haha');
} catch (e) {
	assert.equal(e.message, 'Invalid objectId, got "haha"');
}

try {
	createObjectId();
} catch (e) {
	console.log(e);
}
